$('.galery-product__title > a').each (function () {
    if ($(this).text().length > 50)
        $(this).text($(this).text().substring(0,50) + '...');
});

//Add buttons by hover on the list-products//
$('.gallery-grid--item').hover(function () {
    $(this).find('.product-hover').toggleClass('hidden');
    $(this).find('.product-hover-btns').toggleClass('hidden');
}, function () {
    $(this).find('.product-hover').toggleClass('hidden');
    $(this).find('.product-hover-btns').toggleClass('hidden');
});

//Link
$('.product-hover-btns').click(function () {
    var url = $(this).attr('dataUrl');
    window.location.href = url;
});