jQuery(document).ready(function() {

    function autoHeightAnimate(element, time){
        var curHeight = element.height(),
            autoHeight = element.css({
                height: 'auto',
                visibility: 'visible'
            }).height();
        element.height(curHeight);
        element.stop().animate({ height: autoHeight }, time);
    }

    /*===================SELECT===========================*/

    $('#deliveryCity,#deliveryCityArrow').on('click', function (evt) {
        evt.stopPropagation();
        $('#deliveryCity,#deliveryCityArrow').hide();
        $('.dropdown-click-block .icon-down-arrow').hide();

        $('.chosen-select').chosen({
            width: "100%"
        });

        $('.chosen-select').trigger('chosen:open');
    });


    $('.chosen-select').on('chosen:hiding_dropdown', function () {
        $(this).chosen("destroy");
        $('#deliveryCity').show();
        $('.dropdown-click-block .icon-down-arrow').show();
    });


    $('#deliveryCitySelect').on('change', function(evt, params) {
        var selected = params.selected;
        var city = $('#deliveryCitySelect option[value="' + selected + '"]').html();
        $('#deliveryCity').removeAttr('placeholder').val(city).show();
        $(".chosen-select").chosen("destroy");
    });

});
